// Import FirebaseAuth and firebase.
import React from 'react';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import firebase from 'firebase';
import firebaseui from 'firebaseui'

// Configure Firebase.
const config = {
    apiKey: "AIzaSyCv4aHJcd0K-oNE0OknDxBgr1qYhbIGCYM",
    authDomain: "artixa-1fe69.firebaseapp.com",
    databaseURL: "https://artixa-1fe69.firebaseio.com",
    projectId: "artixa-1fe69",
    storageBucket: "artixa-1fe69.appspot.com",
    messagingSenderId: "624275369219"
};
firebase.initializeApp(config);

// Configure FirebaseUI.
let uiConfig = {
  // Popup signin flow rather than redirect flow.
  signInFlow: 'popup', 
  'credentialHelper': firebaseui.auth.CredentialHelper.NONE,
  // Redirect to /signedIn after sign in is successful. Alternatively you can provide a callbacks.signInSuccess function.
  signInSuccessUrl: '/Fokusz',
  // We will display email as auth providers.
  signInOptions: [
    firebase.auth.EmailAuthProvider.PROVIDER_ID
  ]
};

firebase.auth().onAuthStateChanged(function(user) {
  if (firebase.auth().currentUser.email === "gabor@artixa.hu") {
    this.userRedirect = '/Fokusz'
  }
});
class SignInScreen extends React.Component {
  render() {
    return (
      <div>
        <h1>My App</h1>
        <p>Please sign-in:</p>
        {/* <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={firebase.auth()}/> */}
        <StyledFirebaseAuth uiConfig={uiConfig} firebaseAuth={this.loginRedirect()}/>
      </div>
    );
  }
}

export default SignInScreen;
